import React, { useEffect, useState } from 'react'
import {Head, Logo, Title, Bar, Content} from './styles'
import {Select, InputNumber, DatePicker, Button, List} from 'antd'
import {PlusOutlined} from '@ant-design/icons'
import Highcharts from 'highcharts'
import HighchartsReact from 'highcharts-react-official'
import 'antd/dist/antd.css';

import logo from "../../images/logo.jpeg"

const dataGraph = {
    chart: {
        type: "pie"
    },
    series: [{
        data:[{
                name: "Fixa",
                y: 0.7
            },
            {
                name: "Vari치vel",
                y: 0.3
        }]
    }]
}

const {Option} = Select;

const dataFixa = [
    {
        'data': '20-01-2019',
        'valor': 'R$1.000,00'
    },
    {
        'data': '03-04-2016',
        'valor': 'R$5.000,00'
    }
]

const dataVariavel = [
    {
        'data': '20-01-2019',
        'valor': 'R$1.000,00'
    },
    {
        'data': '03-04-2016',
        'valor': 'R$5.000,00'
    }
]

const Home: React.FC = () => {
    const [option, setOption] = useState("")

    function handleSelect(val: any){
        console.log(option)
        console.log(val)
        setOption(val)
    }
    function handleInput(val: any){
        console.log(val)
    }
    function handleDate(val: any){
        console.log(val._d.toDateString)
    }
    function handleAdd(){

    }
    return (
        <Content>
            <Head>
                <Logo src = {logo} />
            </Head>
            <Title>
                Carteira de Investimentos
            </Title>
            <Bar>
                <h4>Adicionar novo investimento:</h4>
                <h4> Tipo:</h4>
                <Select 
                style={{ width: 120 }} 
                onChange={e=>handleSelect(e)}>                    
                    <Option value="fixa"> Fixa </Option>
                    <Option value="variavel"> Vari치vel </Option>
                </Select>
                <h4> Valor:</h4>
                <InputNumber onChange={e=>handleInput(e)}/>
                <h4> Data:</h4>
                <DatePicker onChange={e=>handleDate(e)}/>
                <Button onClick={handleAdd} shape="circle" icon={<PlusOutlined/>}/>
            </Bar>
            <Bar>
                <div>
                    <h4>Renda Fixa:</h4>
                    <List
                        dataSource={dataFixa}
                        renderItem={item =>(
                            <List.Item>
                                {item.data} {item.valor}
                            </List.Item>
                        )}
                    ></List>
                </div>
                <div>
                    <h4>Renda Vari치vel:</h4>
                    <List
                        dataSource={dataFixa}
                        renderItem={item =>(
                            <List.Item>
                                {item.data} {item.valor}
                            </List.Item>
                        )}
                    ></List>
                </div>
            </Bar>
            <div>
                <HighchartsReact 
                highcharts={Highcharts}
                options={dataGraph}/>
            </div>
        </Content>
    )
}

export default Home
