import React, { useEffect, useState } from 'react'
import {Redirect} from 'react-router-dom'

import { Input, Button } from 'antd'

interface UserData{
    username: string,
    pass: string
}

const Login: React.FC = () => {
    const [username, setUsername] = useState("")
    const [pass, setPass] = useState("")
    const [logged, setLogged] = useState(false)

    useEffect(() => {
        const authenticated = localStorage.getItem("logged")
        if(authenticated){
            setLogged(true)
       }
    }, [])

    useEffect(() => {
        localStorage.setItem('logged', JSON.stringify(logged))
    }, [logged])
    const allowedUser = {
        username: "admin",
        pass: "asenhamaisfacildomundo"
    }

    function handleUsername(val: any){
        console.log(val)
        setUsername(val)
    }

    function handlePass(val: any){
        setPass(val)
    }

    function handleUser(){
        const userInput = {
            username: username,
            pass: pass
        }
        console.log(userInput)
        console.log(allowedUser)
        console.log(logged)
        if(userInput.username === allowedUser.username && userInput.pass === allowedUser.pass){
            console.log("aaaaa")
            logged=true
        }
        setUsername("")
        setPass("")
    }

    return(
        <div>
            <Input placeholder="Username" onChange={(e) => handleUsername(e.target.value)}/>
            <Input placeholder="Senha" onChange={(e) => handlePass(e.target.value)}/>
            <Button onClick={handleUser}>Login</Button>
            {
                logged?
                <Redirect to={{pathname: '/'}}/>:
                <div></div>
            }    
        </div>
    )
}

export default Login

export const isAuthenticated = () => logged
