import React, { useEffect, useState } from 'react'

import axios from 'axios'

import { useSelector, useDispatch } from 'react-redux'

import {Head, Logo, Title, Bar, Content} from './styles'
import {Select, InputNumber, DatePicker, Button, List} from 'antd'
import {PlusOutlined} from '@ant-design/icons'
import Highcharts from 'highcharts'
import HighchartsReact from 'highcharts-react-official'
import 'antd/dist/antd.css';

import { ApplicationState } from '../../store'
import { getInvestimentosRequest, addInvestimentoRequest} from '../../store/modules/investimento/actions'


import logo from "../../images/logo.jpeg"
//import { setUncaughtExceptionCaptureCallback } from 'process'


const {Option} = Select;

interface InvestimentoData {
    _id?: string,
    opcao: string,
    valor: number,
    data: string
}

const Home: React.FC = () => {
    const [opcao, setOpcao] = useState("fixa")
    const [valor, setValor] = useState(0)
    const [data, setData] = useState("01-01-2019")
    const [investimentoList, setInvestimentoList] = useState<InvestimentoData[]>([])
    const [dataFixa, setDataFixa] = useState<InvestimentoData[]>([])
    const [dataVariavel, setDataVariavel] = useState<InvestimentoData[]>([])
    const [perFixa, setPerFixa] = useState(1)
    const [perVariavel, setPerVariavel] = useState(0)
    const [investimento, setInvestimento] = useState("")

    useEffect(() => {
        const investimentos = localStorage.getItem("investimentoList")
        if(investimentos){
            const investimentosParsed = JSON.parse(investimentos)
            setInvestimentoList(investimentosParsed)
            setDataFixa(investimentosParsed.filter((investimento: InvestimentoData) => investimento.opcao === "fixa"))
            setDataVariavel(investimentosParsed.filter((investimento: InvestimentoData) => investimento.opcao === "variavel"))
            var ctFixa = 0, ctVariavel = 0
            investimentosParsed.map((investimento: InvestimentoData )=>
                investimento.opcao === "fixa"? ctFixa++: ctVariavel++                
            )
            setPerFixa(ctFixa/(ctFixa + ctVariavel))
            setPerVariavel(ctVariavel/(ctVariavel + ctFixa))
        }
    }, [])

    useEffect(() => {
        localStorage.setItem('investimentoList', JSON.stringify(investimentoList))
    }, [investimentoList])

    const dispatch = useDispatch()
    const investimentos = useSelector((state: ApplicationState) => state.investimento.investimentos)
    console.log(investimentos)
    setInvestimentoList(investimentos)
    useEffect(() => {
        const investimentos = useSelector((state: ApplicationState) => state.investimento.investimentos)
        console.log(investimentos)
        if(investimentos){
            setInvestimentoList(investimentos)
            setDataFixa(investimentos.filter((investimento: InvestimentoData) => investimento.opcao === "fixa"))
            setDataVariavel(investimentos.filter((investimento: InvestimentoData) => investimento.opcao === "variavel"))
            /*var ctFixa = 0, ctVariavel = 0
            investimentos.map((investimento: InvestimentoData )=>
                investimento.opcao === "fixa"? ctFixa++: ctVariavel++                
            )
            setPerFixa(ctFixa/(ctFixa + ctVariavel))
            setPerVariavel(ctVariavel/(ctVariavel + ctFixa))*/
        }
    }, [])
    /*axios.get("http://localhost:4040/dev/investments")
        .then(function(response) {
            console.log(response)
            setInvestimentoList(response.data)
            setDataFixa(response.data.filter((investimento: InvestimentoData) => investimento.opcao === "fixa"))
            setDataVariavel(response.data.filter((investimento: InvestimentoData) => investimento.opcao === "variavel"))
            var ctFixa = 0, ctVariavel = 0
            response.data.map((investimento: InvestimentoData )=>
                investimento.opcao === "fixa"? ctFixa++: ctVariavel++                
            )
            setPerFixa(ctFixa/(ctFixa + ctVariavel))
            setPerVariavel(ctVariavel/(ctVariavel + ctFixa))
        })
    */
    useEffect(() => {
        console.log("getTodosRequest")
        dispatch(getInvestimentosRequest())
      }, [])

    function handleSelect(val: any){
        setOpcao(val)
    }
    function handleInput(val: any){
        setValor(val)
    }
    function handleDate(val: any, dateString: string){
        setData(dateString)
    }
    function handleAdd(){
        dispatch(addInvestimentoRequest(opcao, valor, data))
    }




    return (

        <Content>
            { console.log(investimentos) }
            <Head>
                <Logo src = {logo} />
            </Head>
            <Title>
                Carteira de Investimentos
            </Title>
            <Bar>
                <h4>Adicionar novo investimento:</h4>
                <h4> Tipo:</h4>
                <Select 
                style={{ width: 120 }} 
                onChange={e=>handleSelect(e)}>                    
                    <Option value="fixa"> Fixa </Option>
                    <Option value="variavel"> Variável </Option>
                </Select>
                <h4> Valor:</h4>
                <InputNumber onChange={e=>handleInput(e)}/>
                <h4> Data:</h4>
                <DatePicker format="DD-MM-YYYY" onChange={handleDate}/>
                <Button onClick={handleAdd} shape="circle" icon={<PlusOutlined/>}/>
            </Bar>
            <Bar>
                <div>
                    <h4>Renda Fixa:</h4>
                    <List
                        dataSource={dataFixa}
                        renderItem={item =>(
                            <List.Item>
                                {item.data} R${item.valor}
                            </List.Item>
                        )}
                    ></List>
                </div>
                <div>
                    <h4>Renda Variável:</h4>
                    <List
                        dataSource={dataVariavel}
                        renderItem={item =>(
                            <List.Item>
                                {item.data} R${item.valor}
                            </List.Item>
                        )}
                    ></List>
                </div>
            </Bar>
            <div>
{/*                 <HighchartsReact 
                highcharts={Highcharts}
                options={{
                    chart: {
                        type: "pie"
                    },
                    series: [{
                        data:[{
                                name: "Fixa",
                                y: perFixa
                            },
                            {
                                name: "Variável",
                                y: perVariavel
                        }]
                    }]
                }}/>  */}
            </div>
        </Content>
    )
}

export default Home
