import React, { useEffect, useState } from 'react'

import { Input, Button } from 'antd'

interface UserData{
    username: string,
    pass: string
}

let logged = false

const Login: React.FC = () => {
    const [username, setUsername] = useState("")
    const [pass, setPass] = useState("")
    const [user, setUser] = useState("")

    const allowedUser = {
        username: "admin",
        pass: "asenhamaisfacildomundo"
    }

    /*useEffect(() => {
        const userA = localStorage.getItem("user")
        if(userA){
            setUser(JSON.parse(userA))
       }
    }, [])

    useEffect(() => {
        localStorage.setItem('user', JSON.stringify(user))
    }, [user])*/

    function handleUsername(val: any){
        setUsername(val)
    }

    function handlePass(val: any){
        setPass(val)
    }

    function handleUser(){
        const userInput = {
            username: username,
            pass: pass
        }
        if(userInput == allowedUser)
            logged!=logged
    }

    return(
        <div>
            <Input onChange={(e) => handleUsername(e)}>Username</Input>
            <Input onChange={(e) => handlePass(e)}>Password</Input>
            <Button onClick={handleUser}>Login</Button>
        </div>
    )
}

export default Login

export const isAuthenticated = () => logged
