import React, { useEffect, useState } from 'react'

import { Input, Button } from 'antd'
import { truncateSync } from 'fs'

interface UserData{
    username: string,
    pass: string
}

let logged = false

const Login: React.FC = () => {
    const [username, setUsername] = useState("")
    const [pass, setPass] = useState("")

    const allowedUser = {
        username: "admin",
        pass: "asenhamaisfacildomundo"
    }

    /*useEffect(() => {
        const userA = localStorage.getItem("user")
        if(userA){
            setUser(JSON.parse(userA))
       }
    }, [])

    useEffect(() => {
        localStorage.setItem('user', JSON.stringify(user))
    }, [user])*/

    function handleUsername(val: any){
        setUsername(val)
    }

    function handlePass(val: any){
        setPass(val)
    }

    function handleUser(){
        const userInput = {
            username: username,
            pass: pass
        }
        if(userInput == allowedUser){
            logged=true
        }
        setUsername("")
        setPass("")
    }

    return(
        <div>
            <Input placeholder="Username" onChange={(e) => handleUsername(e)}/>
            <Input placeholder="Senha" onChange={(e) => handlePass(e)}/>
            <Button onClick={handleUser}>Login</Button>
        </div>
    )
}

export default Login

export const isAuthenticated = () => logged
