import React, { useEffect } from 'react'
import {Head, Logo, Title, Bar, Content} from './styles'
import {Select, InputNumber, DatePicker, Button, List} from 'antd'
import {PlusOutlined} from '@ant-design/icons'
import 'antd/dist/antd.css';

import logo from "../../images/logo.jpeg"

const {Option} = Select;

const dataFixa = [
    {
        'data': '20-01-2019',
        'valor': 'R$1.000,00'
    },
    {
        'data': '03-04-2016',
        'valor': 'R$5.000,00'
    }
]

const dataVariavel = [
    {
        'data': '20-01-2019',
        'valor': 'R$1.000,00'
    },
    {
        'data': '03-04-2016',
        'valor': 'R$5.000,00'
    }
]

const Home: React.FC = () => {
    return (
        <Content>
            <Head>
                <Logo src = {logo} />
            </Head>
            <Title>
                Carteira de Investimentos
            </Title>
            <Bar>
                <h4>Adicionar novo investimento:</h4>
                <h4> Tipo:</h4>
                <Select style={{ width: 120 }} >                

                    <Option value="fixa"> Fixa </Option>
                    <Option value="variavel"> Variável </Option>
                </Select>
                <h4> Valor:</h4>
                <InputNumber></InputNumber>
                <h4> Data:</h4>
                <DatePicker/>
                <Button shape="circle" icon={<PlusOutlined/>}/>
            </Bar>

            <div>
                <div>
                    <h4>Renda Fixa:</h4>
                    <List
                        dataSource={dataFixa}
                        renderItem={item =>(
                            <List.Item>
                                {item.data} {item.valor}
                            </List.Item>
                        )}
                    ></List>
                </div>
                <div>
                    <h4>Renda Variável:</h4>
                    <List
                        dataSource={dataFixa}
                        renderItem={item =>(
                            <List.Item>
                                {item.data} {item.valor}
                            </List.Item>
                        )}
                    ></List>
                </div>
            </div>
        </Content>
    )
}

export default Home
